"use strict";

// ----------jquery----------
// --------------------------
$(function () {
  var home = $('#home');
  var services = $('#services');
  var about = $('#about');
  var work = $('#work');
  var contacts = $('#contacts');
  var homeTop = home.offset().top;
  var servicesTop = services.offset().top;
  var aboutTop = about.offset().top;
  var workTop = work.offset().top;
  var contactsTop = contacts.offset().top;
  var timeAnchor = 600;
  var delta = 20;
  var $innerWidth = $(window).innerWidth();
  var $outerWidth = $(window).outerWidth();
  $('.slick-slider-home').slick({
    speed: 500,
    dots: true,
    slidesToShow: 1,
    slidesToScroll: 1,
    //adaptiveHeight: true,
    autoplay: true,
    autoplaySpeed: 3000
  });
  $(window).resize(function () {
    homeTop = home.offset().top;
    servicesTop = services.offset().top;
    aboutTop = about.offset().top;
    workTop = work.offset().top;
    contactsTop = contacts.offset().top;
  });
  $('.item-home').css({
    'background-color': '#74c7d5'
  });
  $('.item-services').mouseenter(function () {
    $(this).css({
      'background-color': '#df5c64'
    });
  });
  $('.item-services').mouseleave(function () {
    if (pageYOffset < servicesTop - delta || pageYOffset >= aboutTop - delta) {
      $(this).css({
        'background-color': 'transparent'
      });
    }

    ;
  });
  $('.item-about').mouseenter(function () {
    $(this).css({
      'background-color': '#91778e'
    });
  });
  $('.item-about').mouseleave(function () {
    if (pageYOffset < aboutTop - delta || pageYOffset >= workTop - delta) {
      $(this).css({
        'background-color': 'transparent'
      });
    }

    ;
  });
  $('.item-work').mouseenter(function () {
    $(this).css({
      'background-color': '#a1ca6c'
    });
  });
  $('.item-work').mouseleave(function () {
    if (pageYOffset < workTop - delta || pageYOffset >= contactsTop - delta) {
      $(this).css({
        'background-color': 'transparent'
      });
    }

    ;
  });
  $('.item-contacts').mouseenter(function () {
    $(this).css({
      'background-color': '#82c8bd'
    });
  });
  $('.item-contacts').mouseleave(function () {
    if (pageYOffset < contactsTop - delta) {
      $(this).css({
        'background-color': 'transparent'
      });
    }

    ;
  });
  $(window).scroll(function () {
    if (pageYOffset < servicesTop - delta) {
      $('.item-home').css({
        'background-color': '#74c7d5'
      });
    } else {
      $('.item-home').css({
        'background-color': 'transparent'
      });
      $('.item-home').mouseenter(function () {
        $(this).css({
          'background-color': '#74c7d5'
        });
      });
      $('.item-home').mouseleave(function () {
        if (pageYOffset >= servicesTop - delta) {
          $(this).css({
            'background-color': 'transparent'
          });
        }

        ;
      });
    }

    ;

    if (pageYOffset >= servicesTop - delta && pageYOffset < aboutTop - delta) {
      $('.item-services').css({
        'background-color': '#df5c64'
      });
    } else {
      $('.item-services').css({
        'background-color': 'transparent'
      });
      $('.item-services').mouseenter(function () {
        $(this).css({
          'background-color': '#df5c64'
        });
      });
      $('.item-services').mouseleave(function () {
        if (pageYOffset < servicesTop - delta || pageYOffset >= aboutTop - delta) {
          $(this).css({
            'background-color': 'transparent'
          });
        }

        ;
      });
    }

    ;

    if (pageYOffset >= aboutTop - delta && pageYOffset < workTop - delta) {
      $('.item-about').css({
        'background-color': '#91778e'
      });
    } else {
      $('.item-about').css({
        'background-color': 'transparent'
      });
      $('.item-about').mouseenter(function () {
        $(this).css({
          'background-color': '#91778e'
        });
      });
      $('.item-about').mouseleave(function () {
        if (pageYOffset < aboutTop - delta || pageYOffset >= workTop - delta) {
          $(this).css({
            'background-color': 'transparent'
          });
        }

        ;
      });
    }

    ;

    if (pageYOffset >= workTop - delta && pageYOffset < contactsTop - delta) {
      $('.item-work').css({
        'background-color': '#a1ca6c'
      });
    } else {
      $('.item-work').css({
        'background-color': 'transparent'
      });
      $('.item-work').mouseenter(function () {
        $(this).css({
          'background-color': '#a1ca6c'
        });
      });
      $('.item-work').mouseleave(function () {
        if (pageYOffset < workTop - delta || pageYOffset >= contactsTop - delta) {
          $(this).css({
            'background-color': 'transparent'
          });
        }

        ;
      });
    }

    ;

    if (pageYOffset >= contactsTop - delta) {
      $('.item-contacts').css({
        'background-color': '#82c8bd'
      });
    } else {
      $('.item-contacts').css({
        'background-color': 'transparent'
      });
      $('.item-contacts').mouseenter(function () {
        $(this).css({
          'background-color': '#82c8bd'
        });
      });
      $('.item-contacts').mouseleave(function () {
        if (pageYOffset < contactsTop - delta) {
          $(this).css({
            'background-color': 'transparent'
          });
        }

        ;
      });
    }

    ;
  });
  $('*[data-anchor^="#"]').on('click', function (e) {
    e.preventDefault();
    var d = $(this).attr('data-anchor');
    $('html,body').stop().animate({
      scrollTop: $(d).offset().top
    }, timeAnchor);
  }); // ----------------portfolio filter---------------

  var filter = $('[data-filter]');
  filter.on('click', function (event) {
    event.preventDefault();
    var category = $(this).data('filter'); //получаем название категории по клику
    //выбранной категории добавляется класс active-category

    $('[data-filter]').each(function () {
      var activeCategory = $(this).data('filter');

      if (category != activeCategory) {
        $(this).removeClass('active-category');
      } else {
        $(this).addClass('active-category');
      }
    }); //

    $('[data-category]').each(function () {
      var itemCategory = $(this).data('category');

      if (category == 'all') {
        $('[data-category]').removeClass('hide-card');
      } else {
        if (itemCategory != category) {
          $(this).addClass('hide-card');
        } else {
          $(this).removeClass('hide-card');
        }
      }
    });
  }); // ----------------magnific-popup-----------------
  // -----------Dialog with CSS animation-----------

  $('.popup-with-zoom-anim').magnificPopup({
    type: 'inline',
    fixedContentPos: false,
    fixedBgPos: true,
    overflowY: 'hidden',
    closeBtnInside: true,
    preloader: false,
    midClick: true,
    removalDelay: 300,
    mainClass: 'my-mfp-zoom-in',
    callbacks: {
      open: function open() {
        $('body').addClass('no-scroll');

        if ($innerWidth != $outerWidth) {
          $('body').addClass('no-scroll--pr');
          $('.header-wrapper').addClass('no-scroll--pos');
        } else {
          $('#portfolio-card-desc').css({
            'transform': 'translateX(0)'
          });
        }
      },
      close: function close() {
        $('body').removeClass('no-scroll');

        if ($innerWidth != $outerWidth) {
          $('body').removeClass('no-scroll--pr');
          $('.header-wrapper').removeClass('no-scroll--pos');
        } else {
          $('#portfolio-card-desc').css({
            'transform': 'translateX(0)'
          });
        }
      }
    }
  });
}); // --------native js--------
